frappe.ui.form.on("Timesheet",{
	refresh: function(frm) {
		if(cur_frm.doc.docstatus == 0) {
			frm.add_custom_button(__("Add Next Day"), function(foo) {
					var index = frm.doc.time_logs.length - 1;
					if (index >= 0)
					{
						frm.doc.time_logs[index]

						frm.add_child("time_logs");

						var startFirstDay = new Date(frm.doc.time_logs[index].from_time);
						var startNextDay = new Date();
						startNextDay.setTime(startFirstDay.getTime() + 1000*60*60*24);

						var endFirstDay = new Date(frm.doc.time_logs[index].to_time);
						var endNextDay = new Date();
						endNextDay.setTime(endFirstDay.getTime() + 1000*60*60*24);

						frm.doc.time_logs[index+1].activity_type = frm.doc.time_logs[index].activity_type;
						frm.doc.time_logs[index+1].from_time =  startNextDay;
						frm.doc.time_logs[index+1].to_time = endNextDay;
						frm.doc.time_logs[index+1].hours = frm.doc.time_logs[index].hours;
						frm.refresh_field("time_logs");
					}
			});
		}



	}
});
frappe.ui.form.on("Timesheet Detail",{
	set_default_hours: function(frm, cdt, cdn) {
		var d = locals[cdt][cdn];

		var ts = frappe.get_doc(cdt, cdn);

		frappe.call({
				"method": "frappe.client.get",
				args: {
					doctype: "Employee",
					name: frm.doc.employee
				},
				callback: function (data) {
					debugger;
					var from_time = new Date(d.from_time);
					var ft = data.message.start_time.split(':');
					from_time.setHours(ft[0]);
					from_time.setMinutes(ft[1]);
					from_time.setSeconds(ft[2]);

					var to_time = new Date(d.from_time);
					var tt = data.message.end_time.split(':');
					to_time.setHours(tt[0]);
					to_time.setMinutes(tt[1]);
					to_time.setSeconds(tt[2]);

					//frappe.model.set_value(d.doctype, d.name, "from_time", from_time);
					//frappe.model.set_value(d.doctype, d.name, "to_time", to_time);

					d.from_time = from_time
					d.to_time = to_time
					frm.refresh_field("time_logs");
				}
			});

	}
});

frappe.ui.form.on("Payment Entry", "validate", function(frm) {
	$.each(frm.doc.references || [], function(i, row) {
		if (row.reference_doctype === "Purchase Invoice" && row.reference_name !== "") {
			frappe.call ({
				'method': 'frappe.client.get_value',
				'args': {
					'doctype': row.reference_doctype,
					'filters': {'name': row.reference_name},
					'fieldname': ['bill_no']
				},
				callback: function(r) {
						if (!r.exc) {
							if (r.message.bill_no != null)
							{
								row.supplier_invoice= r.message.bill_no;
							}
						}
						frm.refresh_field("references");
				 }
			});
		}
	});	
});
