frappe.ui.form.on("Item",{
	refresh: function(frm) {
		frm.fields_dict.attributes.grid.toggle_enable("attribute_value", 1);

		frm.add_custom_button(__("Copy Pricing from Previous Revision"), function(foo) {
			frappe.call({
				method:"velometro.velometro.supplier_quotation.copy_pricing_rule_from_previous_revision",
				args: {
					"base_item_code": cur_frm.doc.variant_of,
					"current_rev": cur_frm.doc.attributes[0].attribute_value
				}, 
				callback: function(r) { 
					frm.reload_doc();

				}
			});
		});

		frm.add_custom_button(__("Find Parent BOMs"), function(foo) {

			frappe.route_options = {'item_code': ['=', cur_frm.doc.item_code]};
			//if(show_open) {
			//	$.extend(frappe.route_options, frappe.ui.notifications.get_filters(doctype));
			//}
			frappe.set_route("List", 'BOM');
		});
	}
});

